package com.dnaycarreto.myfirstdemoimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.util.Logimport android.view.Viewimport kotlinx.android.synthetic.main.activity_main.*class MainActivity : AppCompatActivity(), View.OnClickListener {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        tvSalute.setOnClickListener(this)        println("OnCreate")    }    override fun onStart() {        super.onStart()        println("OnStart")    }    override fun onResume() {        super.onResume()        Log.i(MainActivity::class.java.simpleName,"OnResume")    }    override fun onPause() {        super.onPause()        Log.wtf(MainActivity::class.java.simpleName,"OnPause")    }    override fun onRestart() {        super.onRestart()        Log.d(MainActivity::class.java.simpleName, "OnRestart")    }    override fun onDestroy() {        super.onDestroy()        Log.e(MainActivity::class.java.simpleName, "OnDestroy")    }    fun toSecond(view: View){        //Log.wtf(MainActivity::class.java.simpleName, "OnClickEvent")        val intent = Intent(this,            SecondActivity::class.java)        startActivity(intent)    }    override fun onClick(view: View?) {        when(view?.id){           tvSalute.id ->{                Log.d(MainActivity::class.java.simpleName, "OnClickInterface")            }        }    }}